import { NuxtModule } from '@nuxt/schema';

interface ModuleOptions {
    defaultConfig?: boolean;
    configFile?: string;
    /**
     * When true FormKit will not install itself globally and will instead inject
     * a `<FormKitLazyProvider>` around components that use FormKit. Additionally
     * when true FormKit enables auto-imports for the following:
     *
     * - `<FormKit>`
     * - `<FormKitProvider>`
     * - `<FormKitMessages>`
     * - `<FormKitSummary>`
     * - `getNode()`
     * - `createInput()`
     * - `setErrors`,
     * - `clearErrors`,
     * - `submitForm`,
     * - `reset`,
     * - `FormKitNode`
     *
     * @experimental
     */
    autoImport?: boolean;
}
declare const module: NuxtModule<ModuleOptions>;

export { type ModuleOptions, module as default };
